link.script:
$(document).ready(function(){
	var active="none";
	$("tr.to_hide").hide();

	$("td.name").click(function(){
		var tmp_id= $(this).attr("id");
		$("tr.to_hide").hide();
		if(active!=tmp_id){
			active = tmp_id;
			$(this).parent().parent().children().show();
		}else{
			active = "none";
		}
	});
});
